(function($){$.fn.icecast = function(options){$.ajaxSetup({cache:true,scriptCharset:"utf-8",contentType:"text/json;charset=utf-8"});var defaults = {server:"",stationlogo:"http://i.imgur.com/L4qoo.gif"};
var options = $.extend(defaults,options);return this.each(function(){var icecast = $(this);
$.getJSON('http://'+options.server+'/json.xsl?callback=',function(data){$.each(data.mounts,function(i,mount){
$(icecast).append('<li class="mount"><a href="'+mount.url+'"><img class="logo" src="'+options.stationlogo+'" alt=""></a><div class="track">'+mount.title+'</div><div class="description">'+mount.server_description+mount.server_name+'</div><div class="genre">Genre: '+mount.genre+'</div><div class="listeners">Listeners: '+mount.listeners+' at '+mount.bitrate+'kbps</div></li>');});});});};})(jQuery);
